const bel = require('bel')
const csjs = require('csjs-inject')

module.exports = footer

function footer () {

  const el = bel`  
    <footer>
      <p class="${css['text--small']}">Shokunin Network 2022 | All Rights Reserved</p>
      <div class=${css.footer__sm}>
        <a href="/">
          <img src="assets/imgs/discord.png" alt="Shokunin discord" />
        </a>
        <a href="/">
          <img src="assets/imgs/twitter.png" alt="Shokunin twitter" />
        </a>
      </div>
    </footer>
  `
  return el
}


const css = csjs`
  :root {
    --transition: all ease 0.5s;
  }
  footer {
    border-top: 1px solid #FFFFFF;
    display: flex;
    justify-content: space-between;
    padding: 32px 72px;
  }
  .text--small {
    font-style: normal;
    font-weight: 400;
    font-size: 20px;
    color: #FFFFFF;
  }
  .footer__sm {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 32px;
  }
  .footer__sm img {
    width: 40px;
    height: 100%;
  }
  .footer__sm img:hover {
    cursor: pointer;
    opacity: 0.7;
    transition: var(--transition);
  }
  @media screen and (max-width: 768px) {
    footer {
        flex-direction: column;
        gap: 32px;
    }
  }
`