const bel = require('bel')
const csjs = require('csjs-inject')

module.exports = footer

function footer () {

  const el = bel`  
  <footer>
    <p class="${css['text--small']}">Shokunin Network 2022 | All Rights Reserved</p>
    <div class=${css.footer__sm}>
      <a href="/">
        <img src="assets/imgs/discord.png" alt="Shokunin discord" />
      </a>
      <a href="/">
        <img src="assets/imgs/twitter.png" alt="Shokunin twitter" />
      </a>
    </div>
  </footer>

  `
  return el
}


const css = csjs`
:root {
  --bgColor: #110042;
  --textColor: #fff;
  --secondary: #C931FF;

  --transition: all ease 0.5s;

  --linkLineHeight: 31px;
  --h1LineHeight: 100px;
  --h2LineHeight: 61px;
  --btnSize: 24px;

  --linkSize: 25px;
  --h1Size: 96px;
  --h2Size: 48px;

  --font500: 500;
  --font700: 700;
  --fontStyletNormal: normal;

  --nav__linksGAP: 32px;

  --btn-maxW: 315px;
  --btn-p: 18px 67px;
}

* {
  box-sizing: border-box;
  padding: 0;
  margin: 0;
  font-family: 'Space Grotesk';
}

body {
  background-color: var(--bgColor);
}

.container {
  max-width: 1512px;
  margin: 0 auto;
}


footer {
  border-top: 1px solid #FFFFFF;
  display: flex;
  justify-content: space-between;
  padding: 32px 72px;
}


.text--small {
  font-style: normal;
  font-weight: 400;
  font-size: 20px;
  color: #FFFFFF;
}


.footer__sm {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 32px;
}

.footer__sm img {
  width: 40px;
  height: 100%;
}

.footer__sm img:hover {
  cursor: pointer;
  opacity: 0.7;
  transition: var(--transition);
}

a {
  font-style: var(--fontStyletNormal);
  font-weight: var(--font500);
  font-size: var(--linkSize);
  line-height: var(--linkLineHeight);
  color: var(--textColor);
  text-decoration: none;
}

/****************************************************/



::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  box-shadow: inset 0 0 5px grey;
  border-radius: 10px;
}

::-webkit-scrollbar-thumb {
  background: var(--secondary);
  border-radius: 10px;
}

::-webkit-scrollbar-thumb:hover {
  background: #6c3181;
}

.ctas {
  display: flex;
  flex-direction: row;
  gap: 32px;
  align-items: center;
}

/* Media Queries */


@media screen and (max-width: 768px) {


  footer {
      flex-direction: column;
      gap: 32px;
  }

}
`