function projects () {

  var el = bel`  
    <div class="${css.container}">
      <div class="${css.projects} ${css.imgBg}">
        <h1>
          BROWSE <br />
          SHOKUNIN <br />
          PROJECTS <br />
        </h1>
      </div>
      <div class="${css['imgBg--grid']}">
        ${
          projectsList.map(item => bel`<img onclick=${() => openSingleProject(item)} class="${css['project-id']}" src="${item.projectMainImg}" alt="" />`)
        }
      </div>
    </div>
  `

  function openSingleProject(item){
    el.innerHTML = ''
    el.append(singleProject_page(item))
    return el

  }
  
  return el
}


const css = csjs`
  :root {
    --bgColor: #110042;
    --textColor: #fff;
    --secondary: #C931FF;
    --transition: all ease 0.5s;
    --linkLineHeight: 31px;
    --h1LineHeight: 100px;
    --h2LineHeight: 61px;
    --btnSize: 24px;
    --linkSize: 25px;
    --h1Size: 96px;
    --h2Size: 48px;
    --font500: 500;
    --font700: 700;
    --fontStyletNormal: normal;
    --nav__linksGAP: 32px;
    --btn-maxW: 315px;
    --btn-p: 18px 67px;
  }
  .container {
    max-width: 1512px;
    margin: 0 auto;
  }
  .projects {
    background-image: url(assets/imgs/Projects.png);
  }
  .imgBg {
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    width: 100%;
    height: auto;
    min-height: 700px;
    padding: 80px 72px;
    display: flex;
    justify-content: space-between;
    flex-direction: column;
  }
  .imgBg--grid {
    width: 100%;
    height: auto;
    min-height: 700px;
    padding: 80px 72px;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    gap: 30px;
  }
  h1 {
    font-style: var(--fontStyletNormal);
    font-weight: var(--font700);
    font-size: var(--h1Size);
    line-height: var(--h1LineHeight);
    color: var(--textColor);
    text-transform: uppercase;
    max-width: 695px;
  }
  .project-id {
    width: 100%;
    height: auto;
  }
  .project-id:hover {
    cursor: pointer;
    transform: translateY(-10px);
    transition: var(--transition);
  }
  @media screen and (max-width: 1440px) {
    :root {
        --btnSize: 18px;
        --linkSize: 18px;
        --h1Size: 72px;
        --h2Size: 32px;
        --linkLineHeight: 31px;
        --h1LineHeight: 72px;
        --h2LineHeight: 61px;
        --btn-maxW: 280px;
        --btn-p: 15px 57px;
    }
  }
  @media screen and (max-width: 957px) {
    .imgBg--grid {
        grid-template-columns: 1fr 1fr;

    }
  }
  @media screen and (max-width: 768px) {
    :root {
        --btnSize: 14px;
        --linkSize: 18px;
        --h1Size: 40px;
        --h2Size: 24px;
        --linkLineHeight: 31px;
        --h1LineHeight: 60px;
        --h2LineHeight: 36px;
        --btn-maxW: 100%;
        --btn-p: 10px 57px;
    }
    .imgBg--grid,
    .project-imgs {
        grid-template-columns: 1fr;
    }
  }
`