const bel = require('bel')
const csjs = require('csjs-inject')

module.exports = proposals

function proposals () {

  const el = bel`  
    <div class="${css.container}">
    <div class="${css.proposals} ${css.imgBg}">
      <h1>
        SOMETHING <br />
        ABOUT <br />
        PROPOSALS <br />
        + FORM
      </h1>
    </div>
    <div class="${css.imgBg}">
      <h2>
        Somehing about proposal rules <br />
        and filling out the form
      </h2>
      <div class="${css.ctas}">
        <a href="/" class="${css.btn} ${css['btn--primary']}">Fill Out Form</a>
        <a href="/" class="${css['btn--outline']}">Proposal Rules</a>
        <a href="/" class="${css.icon}">
          <img src="../../assets/imgs/discordPurple.png" alt="" />
        </a>
      </div>
    </div>
  </div>

  `
  return el
}


const css = csjs`
:root {
  --bgColor: #110042;
  --textColor: #fff;
  --secondary: #C931FF;
  --transition: all ease 0.5s;
  --linkLineHeight: 31px;
  --h1LineHeight: 100px;
  --h2LineHeight: 61px;
  --btnSize: 24px;
  --linkSize: 25px;
  --h1Size: 96px;
  --h2Size: 48px;
  --font500: 500;
  --font700: 700;
  --fontStyletNormal: normal;
  --nav__linksGAP: 32px;
  --btn-maxW: 315px;
  --btn-p: 18px 67px;
}
.container {
  max-width: 1512px;
  margin: 0 auto;
}
.proposals {
  background-image: url(assets/imgs/Proposals.png);
}
.imgBg {
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  width: 100%;
  height: auto;
  min-height: 700px;
  padding: 80px 72px;
  display: flex;
  justify-content: space-between;
  flex-direction: column;
}

h1 {
  font-style: var(--fontStyletNormal);
  font-weight: var(--font700);
  font-size: var(--h1Size);
  line-height: var(--h1LineHeight);
  color: var(--textColor);
  text-transform: uppercase;
  max-width: 695px;
}

h2 {
  font-style: var(--fontStyletNormal);
  font-weight: var(--font500);
  font-size: var(--h2Size);
  line-height: var(--h2LineHeight);
  max-width: 1120px;
  color: var(--textColor);
}

.ctas {
  display: flex;
  flex-direction: row;
  gap: 32px;
  align-items: center;
}

.btn {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: var(--btn-p);
  gap: 10px;

  border-radius: 12px;

  width: fit-content;
  min-width: var(--btn-maxW);
  font-style: var(--fontStyletNormal);
  font-weight: var(--font500);
  font-size: var(--btnSize);
  line-height: 31px;
  /* identical to box height */

  text-align: center;
  border: 3px solid transparent;

}

.btn--outline {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: var(--btn-p);
  gap: 10px;

  border-radius: 12px;

  width: fit-content;
  min-width: var(--btn-maxW);
  font-style: var(--fontStyletNormal);
  font-weight: var(--font500);
  font-size: var(--btnSize);

  text-align: center;

  border: 3px solid #FFFFFF;

  color: var(--textColor);
}

.btn--primary {
  background: #C931FF;
  color: #FFFFFF;
}

.btn--secondary {
  background: var(--bgColor);
  color: #FFFFFF;
}

.btn:hover,
.btn--outline:hover {
  cursor: pointer;
  background-color: var(--textColor);
  color: var(--secondary);
  transition: var(--transition);
}

.icon:hover {
  cursor: pointer;
  opacity: 0.7;
  transition: var(--transition);
}


a {
  font-style: var(--fontStyletNormal);
  font-weight: var(--font500);
  font-size: var(--linkSize);
  line-height: var(--linkLineHeight);
  color: var(--textColor);
  text-decoration: none;
}

@media screen and (max-width: 1440px) {
  :root {
      --btnSize: 18px;
      --linkSize: 18px;
      --h1Size: 72px;
      --h2Size: 32px;

      --linkLineHeight: 31px;
      --h1LineHeight: 72px;
      --h2LineHeight: 61px;

      --btn-maxW: 280px;
      --btn-p: 15px 57px;

  }

}

@media screen and (max-width: 768px) {
  :root {
      --btnSize: 14px;
      --linkSize: 18px;
      --h1Size: 40px;
      --h2Size: 24px;

      --linkLineHeight: 31px;
      --h1LineHeight: 60px;
      --h2LineHeight: 36px;

      --btn-maxW: 100%;
      --btn-p: 10px 57px;

  }

  .imgBg--grid,
  .project-imgs {
      grid-template-columns: 1fr;
  }

  nav {
      flex-direction: column;
  }

  footer {
      flex-direction: column;
      gap: 32px;
  }

  .ctas {
      flex-direction: column;
  }

}
`